async function seedCityMissions() {
  const result = await db.execute<{ count: string }>(
    "SELECT count(*)::text as count FROM city_missions"
  );
  const rows = result as any;
  const count = parseInt(rows?.rows?.[0]?.count || rows?.[0]?.count || "0");
  if (count > 0) {
    console.log("Las misiones de ciudad ya estan en la base de datos. Saltando seed de misiones de ciudad.");
    return;
  }

  console.log("Insertando misiones de Ciudad del Reino...");
  for (const m of CITY_MISSION_DATA) {
    await db.insert(cityMissions).values(m);
  }
  console.log(`${CITY_MISSION_DATA.length} misiones de ciudad insertadas.`);
}

export async function seedGameData() {
  const existing = await db.select().from(gameQuestions);
  if (existing.length > 0) {
    const existingQuestions = new Set(existing.map(q => q.question));
    const newQuestions = questions.filter(q => !existingQuestions.has(q.question));
    if (newQuestions.length > 0) {
      console.log(`Insertando ${newQuestions.length} preguntas nuevas...`);
      for (const q of newQuestions) {
        await db.insert(gameQuestions).values(q);
      }
      console.log(`${newQuestions.length} preguntas nuevas insertadas.`);
    } else {
      console.log("Todas las preguntas ya existen. Saltando.");
    }
  } else {
    console.log("Insertando preguntas del juego...");
    for (const q of questions) {
      await db.insert(gameQuestions).values(q);
    }
    console.log(`${questions.length} preguntas insertadas.`);

    console.log("Insertando misiones...");
    for (const m of missions) {
      await db.insert(gameMissions).values(m);
    }
    console.log(`${missions.length} misiones insertadas.`);
  }

  const existingChapters = await db.select().from(storyChapters).limit(1);
  if (existingChapters.length > 0) {
    console.log("Capitulos del modo historia ya existen. Saltando seed.");
  } else {
    console.log("Insertando capitulos del modo historia...");
    for (const ch of storyChaptersData) {
      const [inserted] = await db.insert(storyChapters).values(ch).returning();
      const activities = storyActivitiesData[ch.chapterNumber];
      if (activities) {
        for (const act of activities) {
          await db.insert(storyActivities).values({ ...act, chapterId: inserted.id });
        }
        console.log(`  Capitulo ${ch.chapterNumber}: ${activities.length} actividades`);
      }
    }
    console.log(`${storyChaptersData.length} capitulos insertados con sus actividades.`);
  }

  await seedCityMissions();
}
